import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector } from 'react-redux';
import { Fragment, useEffect , useState} from 'react';
import Notification from './components/UI/Notification';

function App() {

  const addCartItem = useSelector(state => state.addCartItem.items)

  const [isError , setIsError] = useState(false)

  const [isSuccess , setIsSuccess] = useState(false)


  useEffect( () => {

    const res = fetch('https://book-shop-dc783-default-rtdb.firebaseio.com/cart.json',{
      method : 'PUT',
      body : JSON.stringify(addCartItem),
      headers : {
        'content-type' : 'application/json'
      }
    })

    res.then(res => {
       if(res.ok){
            res.json().then(data => {
                console.log(data)
                setIsSuccess(true)
            })
       }
       else{
            console.log('Error has occured')
            setIsError(true)
       }
    })



} ,[addCartItem])



  return (
    <Fragment>
        {isSuccess && <Notification   status='success' title='Success' message='Send cart data successfull.'/>}
        {isError && <Notification status='error' title='Error!' message='Send cart data failed!'/>}
        <Layout>
          <Cart />
          <Products />
        </Layout>
    </Fragment>
  );
}

export default App;
