import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector } from 'react-redux';
import { Fragment, useEffect , useState} from 'react';

function App() {

  const addCartItem = useSelector(state => state.addCartItem.items)

  const [isSending , setIsSending] = useState(false)
  const [isSendData , setIsSendData] = useState(false)
  const [isSuccess , setIsSuccess] = useState(false)
  const [isError , setIsError] = useState(false)

  let para1 = '';
  let para2 = '';

  let msgClass = '';


  useEffect( () => {

    setIsSendData(true)

    const res = fetch('https://book-shop-dc783-default-rtdb.firebaseio.com/cart.json',{
      method : 'PUT',
      body : JSON.stringify(addCartItem),
      headers : {
        'content-type' : 'application/json'
      }
    })

    setIsSending(true);

    res.then(res => {



       if(res.ok){
            res.json().then(data => {
                console.log(data)
                setIsSending(false)
                para1 = 'Success';
                para2 = 'Send cart data successfull';
                msgClass = 'success'

            })
       }
       else{
            // alert('Error has occured')
            setIsSending(false)
            para1 = 'Error!';
            para2 = 'Send cart data failed!';
            msgClass = 'error'
       }
    })



} ,[addCartItem])

if(isSending){
  para1 = 'Sending'
}
if(isSuccess){
  para1 = 'Success'
}
if(isError){
  para1= 'Error'
}



  return (
    <Fragment>
        {isSendData && 
          <div  >
               <div className='col-lg-6 col-md-6 col-12 p-3'>
                    <p>{para1}</p>
               </div>
               <div className='col-lg-6 col-md-6 col-12 d-flex justify-content-end p-3'>
                     <p>{para2}</p>
               </div>
          </div>
        }
        <Layout>
          <Cart />
          <Products />
        </Layout>
    </Fragment>
  );
}

export default App;
