import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector } from 'react-redux';
import { Fragment, useEffect , useState} from 'react';

function App() {

  const addCartItem = useSelector(state => state.addCartItem.items)

  const [isSending , setIsSending] = useState(false)

  let para1 = '';
  let para2 = '';

  let msgClass = '';


  useEffect( () => {

    const res = fetch('https://book-shop-dc783-default-rtdb.firebaseio.com/cart.json',{
      method : 'PUT',
      body : JSON.stringify(addCartItem),
      headers : {
        'content-type' : 'application/json'
      }
    })

    setIsSending(true);
    para1='Sending...';
    para2 = 'Send cart data!';
    msgClass = 'sending'

    res.then(res => {

       if(res.ok){
            res.json().then(data => {
                console.log(data)
                setIsSending(false)
                para1 = 'Success';
                para2 = 'Send cart data successfull';
                msgClass = 'success'

            })
       }
       else{
            // alert('Error has occured')
            setIsSending(false)
            para1 = 'Error!';
            para2 = 'Send cart data failed!';
            msgClass = 'error'
       }
    })

} ,[addCartItem])



  return (
    <Fragment>
        {isSending && 
          <div className={`${msgClass} d-flex`} >
               <div className='col-lg-6 col-md-6 col-12 p-3'>
                    <p>Sending ...</p>
               </div>
               <div className='col-lg-6 col-md-6 col-12 d-flex justify-content-end p-3'>
                     <p>Sending Cart data!</p>
               </div>
          </div>
        }
        <Layout>
          <Cart />
          <Products />
        </Layout>
    </Fragment>
  );
}

export default App;
