// if(isSuccess){
//   para1 = 'Success'
// }
// if(isError){
//   para1= 'Error'
// }

// setIsSuccess(false)
// setIsError(false)



  return (
    <Fragment>
        {isSendData && 
          <div  >
               <div className='col-lg-6 col-md-6 col-12 p-3'>
                    <p>{para1}</p>
               </div>
               <div className='col-lg-6 col-md-6 col-12 d-flex justify-content-end p-3'>
                     <p>{para2}</p>
               </div>
          </div>
        }
        <Layout>
          <Cart />
          <Products />
        </Layout>
    </Fragment>
  );
}

export default App;
